import { Text, TextInput, TouchableOpacity, View, Alert } from 'react-native';
import { styles } from './styles'
import { useState } from 'react';

import axios from 'axios';

export default function Home(){

    const [name, setName] = useState('');
    const [email, setEmail] = useState('');

    const handleCreateUser = async () => {
        try {
          // Substitua a URL abaixo pela sua rota de criação de usuário
          const response = await axios.post('http://localhost:3333/users/create', {
            name,
            email,
          });
    
          // Exemplo de como lidar com a resposta da API
          if (response.data.success) {
            console.log('Cadastro realizaod com sucesso')
            Alert.alert('Cadastro realizado com sucesso!');
          } else {
            Alert.alert('Erro no cadastro. Tente novamente.');
          }
        } catch (error) {
          console.error('Erro ao cadastrar usuário:', error);
          Alert.alert('Erro ao cadastrar usuário. Tente novamente.');
        }
      };

    return(
        <View style={styles.container}>
            <Text style={styles.title}>Cadastro de Usuário</Text>

            <Text>Nome:</Text>
            <TextInput
                style={styles.input}
                value={name}
                onChangeText={ setName }
                placeholder="Digite seu nome"
            />

            <Text>Email:</Text>
            <TextInput
                style={styles.input}
                value={email}
                onChangeText={ setEmail }
                placeholder="Digite seu email"
                keyboardType="email-address"
            />
            

            <TouchableOpacity style={styles.btn} onPress={handleCreateUser}>
                <Text style={styles.btnTxt}>Cadastrar</Text>
            </TouchableOpacity>
        </View>
    )
}
